// src/components/StocksList.js
import React from 'react';
import { Row, Col } from 'react-bootstrap';
import StockPriceChart from './StockPriceChart';

function StocksList({ stocks }) {
  return (
    <div>
      {stocks.map((stock) => (
        <Row key={stock.id} className="mb-4">
          <Col md={6}>
            <StockPriceChart stock={stock} />
          </Col>
          <Col md={6}>
            <h5>{stock.name}</h5>
            <p>Current Price: ${stock.currentPrice.toFixed(2)}</p>
            {/* Additional stock details can be added here */}
          </Col>
        </Row>
      ))}
    </div>
  );
}

export default StocksList;
