// src/components/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import SimulationControl from '../components/SimulationControl';
// import StocksList from '../components/StocksList';
import NavigationBar from '../components/Navbar';

function App() {
  const [simulationRunning, setSimulationRunning] = useState(false);
  const [simulationData, setSimulationData] = useState({ stocks: [], autoplayers: [] });
  const [error, setError] = useState(null);

  const startSimulation = (parameters) => {
    fetch('http://localhost:3001/api/start', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(parameters),
    })
      .then((response) => response.json())
            .then((data) => {
        if (data.error) {
          setError(data.error);
        } else {
          setSimulationRunning(true);
          setError(null);
        }
      })
      .catch((err) => {
        setError('Failed to start simulation.');
      });
     
  };

  console.log(simulationRunning);

  const stopSimulation = () => {
    fetch('http://localhost:3001/api/stop')
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          setError(data.error);
        } else {
          setSimulationRunning(false);
          setError(null);
        }
      })
      .catch((err) => {
        setError('Failed to stop simulation.');
      });
  };

// Your other state and functions here...


  return (
    <Router>
      <NavigationBar />
      <div className="container">
        <h1>Stock Market Simulator</h1>
        {error && <div className="alert alert-danger">{error}</div>}
        <SimulationControl
          onSimulationStart={startSimulation}
          onSimulationStop={stopSimulation}
          simulationRunning={simulationRunning}
        />
        {/* <StocksList stocks={simulationData.stocks} /> */}
      </div>
    </Router>
  );
}

export default App;
