// src/components/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import SimulationControl from './SimulationControl';
import Stocks from './Stocks';
import AutoplayerList from './AutoplayerList';
import NavigationBar from './Navbar';

function App() {
  const [simulationRunning, setSimulationRunning] = useState(false);
  const [simulationData, setSimulationData] = useState({ stocks: [], autoplayers: [] });
  const [error, setError] = useState(null); // Add this line to declare the error state

  // Your other state and functions here...

  return (
    <Router>
      <NavigationBar />
      <div className="container">
        <h1>Stock Market Simulator</h1>
        {error && <div className="alert alert-danger">{error}</div>}
        <SimulationControl
          onSimulationStart={startSimulation}
          onSimulationStop={stopSimulation}
          simulationRunning={simulationRunning}
        />
        <Routes>
          <Route
            path="/"
            element={
              <Stocks
                simulationRunning={simulationRunning}
                stocks={simulationData.stocks}
              />
            }
          />
          <Route
            path="/stocks"
            element={
              <Stocks
                simulationRunning={simulationRunning}
                stocks={simulationData.stocks}
              />
            }
          />
          <Route
            path="/autoplayers"
            element={
              <AutoplayerList
                simulationRunning={simulationRunning}
                autoplayers={simulationData.autoplayers}
              />
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
